INSERT INTO "City" (name, images, description, description_detail) VALUES 
    ('Barcelona', ARRAY ['Barcelona.png', 'bcn-cover.jpeg'], 'Barcelona es conocida por su vibrante comunidad LGTBIQ, con eventos como el Barcelona Pride y una escena nocturna inclusiva que refleja su apoyo a la diversidad.', 'Barcelona es una ciudad cosmopolita, abierta, donde la cohesión social es una realidad.'),
    ('Madrid',ARRAY ['Madrid.png', 'bcn-cover.jpeg'], 'Madrid destaca como un epicentro LGTBIQ en España, con barrios como Chueca que celebran la diversidad con orgullo y eventos como el WorldPride, atrayendo a personas de todo el mundo.', ''),
    ('Ibiza', ARRAY ['Ibiza.png', 'bcn-cover.jpeg'], 'Ibiza ofrece un ambiente de apertura y aceptación, con fiestas inclusivas y espacios seguros para la comunidad LGTBIQ, convirtiéndola en un destino popular para personas de todas las orientaciones sexuales.',''),
    ('Málaga',ARRAY ['Malaga.png', 'bcn-cover.jpeg'],'Málaga ofrece una atmósfera cosmopolita y tolerante, con una escena LGTBIQ vibrante y eventos como el Festival Internacional de Cine LGTBIQ+ que promueven la inclusión y la diversidad en la ciudad.',''),
    ('Gran Canaria', ARRAY['GranCanarias.png', 'bcn-cover.jpeg'], 'Gran Canaria es reconocida por su comunidad diversa y acogedora, especialmente en lugares como Maspalomas, que alberga eventos como el Maspalomas Gay Pride, atrayendo a visitantes de todas partes del mundo.', '')